import React, { useEffect,useState } from 'react'
import './Timer.css'

const Timer = ({duration}) => {
    const [time, setTime] = useState(duration);
    useEffect(()=> {
        setTimeout(() =>{
            setTime(time - 1000);
        }, 1000);
    },[time]);
    
  return (
  <div>{time}</div>
)
  
}

export default Timer
